//available lookup tables,
def lookupTableIds = ["borehole_status": "6b020fbf-5f11-4422-8bf0-8922baa7513d",
					  "borehole_drillingMethod":"3f3e28f7-2c75-4d2e-96eb-0265928de559",
					  "borehole_purpose":"701e44eb-184b-4283-9ec4-4861bc83b682",					  
					  "layer_casingKind":"994bcdea-12b9-4671-bde2-a1df4e1be3d9",
					  "layer_casingMaterial":"e9317ef4-c273-47e4-a326-ab3e39a669bd",					  
					  "layer_chronostratigraphy":"f5433b9b-a89e-4860-a4c7-f8c3ffe8d6ee",
