def stratigraphyList = _source.links.stratigraphy.list()
def countBoreholeFilling = 0
_target {
	//loop over all instances in source table 'stratigraphy'
	stratigraphyList.each { stratigraphy ->
		stratigraphy_kind_id = stratigraphy.p.kind_id_cli.value()		
		
		//create borehole.intervalSeries for each stratigraphy if startigraphy.kind_id = 3000 (Geology)
		if (stratigraphy_kind_id == 3000) {
			
			intervalSeries {
				IntervalSeries {
					description {
						LocalisedCharacterString( stratigraphy.p.name_sty.value() )
					}
					
					//create related layers
					stratigraphy.links.layer.list().each { sourceLayer ->
						layer {
							Interval {
								
								
								from( sourceLayer.p.depth_from_lay.value() ){
									uom( 'm' )
								}
								to( sourceLayer.p.depth_to_lay.value() ){
									uom( 'm' )
								}
								
								def waterContentValue = getLookupTableValue(sourceLayer.p.humidity_id_cli.value(), lookupTableIds["layer_humidity"])
								if (waterContentValue){
									waterContent(waterContentValue){
										codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/WaterContentList.xml' )
									}
								}
								
								def consistanceValue = getLookupTableValue( sourceLayer.p.consistance_id_cli.value(), lookupTableIds["layer_consistance"] )
								if (consistanceValue){
									consistency( consistanceValue ) {
										codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/ConsistencyList.xml' )
									}
								}
								
								def compactnessValue = getLookupTableValue( sourceLayer.p.compactness_id_cli.value(), lookupTableIds["layer_compactness"] )
								if (compactnessValue){
									compactness( compactnessValue ) {
										codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/CompactnessList.xml' )
									}
								}
								
												
								def lithology_code = getCollectedCodeById( sourceLayer.p.lithology_id_cli.value() )
								def lithology_text = getCollectedValueById( sourceLayer.p.lithology_id_cli.value() )
								
								rockCode( lithology_code )
								rockNameText {
									LocalisedCharacterString( lithology_text )
								}
								
								def lithologyValue = getLookupTableValue( sourceLayer.p.lithology_id_cli.value(), lookupTableIds["layer_lithology"] )
								if (lithologyValue) {
									lithology {
										Lithology {
											rockName( lithologyValue ){
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/RockNameList.xml' )
											}
											
											//get rockColors from table layer_codelist as list (as boreholeML codelist values)
											def layer_codelist_colours = []
											sourceLayer.links.layer_codelist.each {item ->
												if(item.p.code_cli.value() == 'mlpr112'){
													def colour = getLookupTableValue( item.p.id_cli_fk.value(), lookupTableIds["layer_rockColour"] )
													layer_codelist_colours.add(colour)
												}
											}
											//set rockColor if the source data contains not more than one colour
											if (layer_codelist_colours.size() == 1){
												rockColor( layer_codelist_colours[0] ) {
													codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/RockColorList.xml' )
												}
											}
											
											
										}
									}
								}
								
								
								_b.stratigraphy {
									Stratigraphy {
										def chronoStrat = getLookupTableValue( sourceLayer.p.chronostratigraphy_id_cli.value(), lookupTableIds["layer_chronostratigraphy"] )
										if (chronoStrat){
											chronoStratigraphy( chronoStrat ) {
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/ChronoStratigraphyList.xml' )
											}
										}
										
										def lithoStrat = getCollectedValueById(sourceLayer.p.lithostratigraphy_id_cli.value())
										if (lithoStrat) {
											lithoStratigraphy {
												LocalisedCharacterString( lithoStrat )
											}
										}
									}
								}
								
								
								stratigraphy.links.lithological_description.each { lithDescription ->
									if ( (sourceLayer.p.depth_from_lay.value() >= lithDescription.p.depth_from.value()) && (sourceLayer.p.depth_to_lay.value() <= lithDescription.p.depth_to.value()) ){
										
										descriptiveText {
											LocalisedCharacterString( lithDescription.p.description.value() )
										}
										
									}
								}
								
								
								
							}
						}
					}	
				}
			}
		}
		//map borehole Filling, if stratigraphy.kind_id = 3004 (Filling) 
		//and if layer.fill_kind_id = 25000302 (Rohrlochverfüllung/casing plugging) 
		//or 25000300 (borehole plugging/Bohrlochverfüllung)
		else if (stratigraphy_kind_id == 3004) {
			stratigraphy.links.layer.list().each { sourceLayer ->
				
				if ((sourceLayer.p.fill_kind_id_cli.value() == 25000302 || sourceLayer.p.fill_kind_id_cli.value() == 25000300) && countBoreholeFilling < 1) {
					countBoreholeFilling += 1
					
					boreholeFilling {
						BoreholeFilling {
							
							from( sourceLayer.p.depth_from_lay.value() ) {
								uom( 'm' )
							}
							
							to( sourceLayer.p.depth_to_lay.value() ) {
								uom( 'm' )
							}
							
							def boreholeFillingValue = getLookupTableValue( sourceLayer.p.fill_material_id_cli.value(), lookupTableIds["layer_fillMaterial"] )
							if (boreholeFillingValue) {
								boreholeFillingMaterial( boreholeFillingValue ) {
									codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/FillingMaterialList.xml' )
								}
							}
							
						}
					}
				}
				else if (countBoreholeFilling >= 1){
					_log.info("Für Bohrloch ${_source.p.id_bho.value()} wurde mehr als eine Bohrloch- oder Ringraumverfüllung angegeben. Es wurde nur die erste vorkommende Verfüllung in die Zieldaten nach borehole.BoreholeFilling übernommen.")
				}
			}
		}
		
	}	
	
	
	//create mandatory attribute borehole.installationDetail	
	installationDetail {
		
		//check if installation details exists in source data
		def installationDetailsExist = false
		stratigraphyList.each { stratigraphy -> 
			if (stratigraphy.p.kind_id_cli.value() == 3002) {
				installationDetailsExist = true
			}
			stratigraphy.links.layer.list().each { sourceLayer ->
				if (sourceLayer.p.fill_kind_id_cli.value() == 25000301){
					installationDetailsExist = true
				}
			}
		}
		
		//map installation details if exists in source data
		if (installationDetailsExist) {
			InstallationDetails {
				
				def casingStrNumber = 0
				//loop through all stratigraphy instances
				stratigraphyList.each { stratigraphy -> 
					
					//create casingString if straigraphy.kind_id = 3002 (Casing)
					if (stratigraphy.p.kind_id_cli.value() == 3002) {
						casingString {
							CasingString {
								
								casingStringNumber( casingStrNumber )
								casingStrNumber += 1
								
								casingStringType( 'un' ) {
									codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/CasingStringTypeList.xml' )
								}
								
								//create related segments
								stratigraphy.links.layer.list().each { sourceLayer ->
									casingStringSegment {
										CasingStringSegment {
											
											from( sourceLayer.p.depth_from_lay.value() ) {
												uom( 'm' )
											}
											
											to( sourceLayer.p.depth_to_lay.value() ) {
												uom( 'm' )
											}
											
											def casingMaterialValue = getLookupTableValue( sourceLayer.p.casng_material_id_cli.value(), lookupTableIds["layer_casingMaterial"] )
											if (casingMaterialValue){
												casingMaterial( casingMaterialValue ) {
													codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/CasingMaterialList.xml' )
												}
											}
											
											diameter( sourceLayer.p.casng_outer_diameter_lay.value() ) {
												uom( 'mm' )
											}
											
											if(sourceLayer.p.casng_outer_diameter_lay.value() && sourceLayer.p.casng_inner_diameter_lay.value()) {
												wallThickness( sourceLayer.p.casng_outer_diameter_lay.value() - sourceLayer.p.casng_inner_diameter_lay.value() ) {
													uom( 'mm' )
												}
											}
											
											def installationElementValue = getLookupTableValue( sourceLayer.p.casng_kind_id_cli.value(), lookupTableIds["layer_casingKind"] )
											if (installationElementValue){
												installationElement( installationElementValue ) {
													codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/InstallationElementList.xml' )
												}
											}	
										}
									}
								}
							}
						}
					}
				
					//create spaceFilling if startigraphy.kind_id = 3004 (Filling) 
					//(and fill_kind_id_cli = 25000301 (annular sealing/Ringraumverfüllung) )
					else if (stratigraphy.p.kind_id_cli.value() == 3004) {
						stratigraphy.links.layer.list().each { sourceLayer ->
							if (sourceLayer.p.fill_kind_id_cli.value() == 25000301){
								spaceFilling {
									SpaceFilling {
											
										from( sourceLayer.p.depth_from_lay.value() ){
											uom( 'm' )
										}
										
										to( sourceLayer.p.depth_to_lay.value() ){
											uom( 'm' )
										}
										
										def fillingMaterialValue = getLookupTableValue( sourceLayer.p.fill_material_id_cli.value(), lookupTableIds["layer_fillMaterial"] )
										if (fillingMaterialValue){
											spaceFillingMaterial( fillingMaterialValue ) {
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/FillingMaterialList.xml' )
											}
										}
									
									}
								}
							}
						}
					}
							
				}
			}
		}
		//if no installation detail exists in source data assign a nilReason
		else {
			nilReason( 'unknown' )
		}
	}	
	
	
}


def getLookupTableValue(value, tableId){
	// retrieve Lookup table
	LookupService ls = HaleUI.getServiceProvider().getService(LookupService.class);
	LookupTableInfo lithTable = ls.getTable(tableId);
	def table = lithTable.getTable();
	
	//get value
	def result = table.lookup(Value.of(value))
	return result
}

def getCollectedValueById(codelistId) {
	def c = _.context.collector()
	def textEn = c.codelist[codelistId].value()	
	return textEn
}

def getCollectedCodeById(codelistId) {
	def c = _.context.collector()
	def code = c.codelist_id_code[codelistId].value()	
	return code
}

//this groovy script collects all ids and text values of codelist
//and all codes of codelist
//no target is created

withTransformationContext {
	def c = _.context.collector(it)
	
	def codelist_ids = _source.p.id_cli.value()
	def codelist_text = _source.p.text_cli_en.value()
	
	c.codelist[codelist_ids] << codelist_text
	
	def codelist_code = _source.p.code_cli.value()
	c.codelist_id_code[codelist_ids] << codelist_code
}

