complexParameter.core:text:
def getCodelistValueById(codelistId) {
	def c = _.context.collector()
	def textEn = c.codelist[codelistId].value()	
	return textEn
}

def getWaterContentListValue(codelistId){
	def sourceValue = getCodelistValueById(codelistId)
	
	switch (sourceValue) {
		case 'dry':
		return 'feu1'
		
		case 'earth-moist':
		return 'feu3'
		
		case 'moist':
		return 'feu4'
		
		case 'wet':
		return 'feu5'
		
		case 'not specified':
		break
	}
}

def getConsistencyListValue(codelistId){
	def sourceValue = getCodelistValueById(codelistId)
	
	switch (sourceValue) {
		case 'very soft':
		return 'bre '
		
		case 'soft':
		return 'wh '
		
		case 'firm':
		return 'hfe '
		
		case 'stiff':
		return 'stf '
		
		case 'hard':
		return 'fe '
		
		case 'very hard':
		return 'fe '
		
		case 'not specified':
		break
	}
}

def getCompactnessListValue(codelistId){
	def sourceValue = getCodelistValueById(codelistId)
	
	switch (sourceValue) {
		case 'very loose':
		return 'ld1 '
		
		case 'loose':
		return 'ld2 '
		
		case 'moderately loose':
		return 'ld3 '
		
		case 'dense':
		return 'ld4 '
		
		case 'very dense':	
		return 'ld5 '
		
		case 'not specified':	
		break
	}
}

