def getCasingMaterialListValue(codelistId){
	def codelistTextEn = getCodelistValueById(codelistId)
	
	switch (codelistTextEn) {
		case 'other ':
		return 'sonst'
		
		case 'not specified':
		return 'NB'
		
		case 'steel':
		return 'st'
		
		case 'stainless steel':
		return 'rf'
		
		case 'concrete':
		return 'bet'
		
		case 'PVC (polyvinyl chloride)':
		return 'pvc'
		
		case 'HDPE (high density polyethylene)':
		return 'hdpe'
		
		case 'plastic (unspecified)':
		return 'k'
	}
}


