def getCodelistCodeById(codelistId) {
	def c = _.context.collector()
	def code = c.codelist_id_code[codelistId].value()	
	return code
}



def stratigraphyList = _source.links.stratigraphy.list()
_target {
	stratigraphyList.each { stratigraphy ->
		stratigraphy_kind_id = stratigraphy.p.kind_id_cli.value()	
		//create interval series if startigraphy.kind_id = 3000 (Geology)	
		if (stratigraphy_kind_id == 3000) {
			
			intervalSeries {
				IntervalSeries {
					description {
						LocalisedCharacterString( stratigraphy.p.name_sty.value() )
					}
					
					//create related layers
					stratigraphy.links.layer.list().each { sourceLayer ->
						layer {
							Interval {
								
								
								from( sourceLayer.p.depth_from_lay.value() ){
									uom( 'm' )
								}
								to( sourceLayer.p.depth_to_lay.value() ){
									uom( 'm' )
								}
								
								def waterContentValue = getWaterContentListValue( sourceLayer.p.humidity_id_cli.value() )
								if (waterContentValue){
									waterContent(waterContentValue){
										codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/WaterContentList.xml' )
									}
								}
								
								def consistanceValue = getConsistencyListValue( sourceLayer.p.consistance_id_cli.value() )
								if (consistanceValue){
									consistency( consistanceValue ) {
										codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/ConsistencyList.xml' )
									}
								}
								
								def compactnessValue = getCompactnessListValue( sourceLayer.p.compactness_id_cli.value() )
								if (compactnessValue){
									consistency( compactnessValue ) {
										codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/CompactnessList.xml' )
									}
								}
								
														
								def lithology_code = getCodelistCodeById( sourceLayer.p.lithology_id_cli.value() )
								def lithology_text = getCodelistValueById( sourceLayer.p.lithology_id_cli.value() )
								
								rockCode( lithology_code )
											rockNameText {
									LocalisedCharacterString( lithology_text )
								}
								
								def lithology = getRockNameListValue( sourceLayer.p.lithology_id_cli.value() )
								if (lithology) {
									_b.lithology {
										Lithology {
											rockName( lithology ){
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/RockNameList.xml' )
											}
										}
									}
								}
								
								_b.stratigraphy {
									Stratigraphy {
										def chronoStrat = getChronoStratListValue( sourceLayer.p.chronostratigraphy_id_cli.value() )
										if (chronoStrat){
											chronoStratigraphy( chronoStrat ) {
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/ChronoStratigraphyList.xml' )
											}
										}
										
										def lithoStrat = getCodelistValueById(sourceLayer.p.lithostratigraphy_id_cli.value())
										if (lithoStrat) {
											lithoStratigraphy {
												LocalisedCharacterString( lithoStrat )
											}
										}
									}
								}
								
								
								
							}
						}
					}	
				}
			}
		}
		
		
		//create casingString if straigraphy.kind_id = 3002 (Casing)
		if (stratigraphy_kind_id == 3002) {
			installationDetail {
				InstallationDetails {
					casingString {
						CasingString {
						
							//create related segments
							stratigraphy.links.layer.list().each { sourceLayer ->
								casingStringSegment {
									CasingStringSegment {
										
										from( sourceLayer.p.depth_from_lay.value() ) {
											uom( 'm' )
										}
										
										to( sourceLayer.p.depth_to_lay.value() ) {
											uom( 'm' )
										}
										
										def casingMaterialValue = getCasingMaterialListValue( sourceLayer.p.casng_material_id_cli.value() )
										if (casingMaterialValue){
											casingMaterial( casingMaterialValue ) {
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/CasingMaterialList.xml' )
											}
										}
										
										diameter( sourceLayer.p.casng_outer_diameter_lay.value() ) {
											uom( 'mm' )
										}
										
										wallThickness( sourceLayer.p.casng_outer_diameter_lay.value() - sourceLayer.p.casng_inner_diameter_lay.value() ) {
											uom( 'mm' )
										}
										
										def installationElementValue = getInstallationElementListValue( sourceLayer.p.casng_kind_id_cli.value() )
										if (installationElementValue){
											installationElement( installationElementValue ) {
												codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/InstallationElementList.xml' )
											}
										}	
									}
								}
							}
						}
					}
				}
			}
		}
		
		
		//create spaceFilling if startigraphy.kind_id = 3004 (Filling)	
		if (stratigraphy_kind_id == 3004) {
			installationDetail {
				InstallationDetails {
					
					//create related segments if fill_kind_id_cli = 25000301 (Ringraumverf√ºllung)
					stratigraphy.links.layer.list().each { sourceLayer ->
						if (sourceLayer.p.fill_kind_id_cli.value() == 25000301){
							spaceFilling {
								SpaceFilling {
									
									from( sourceLayer.p.depth_from_lay.value() ){
										uom( 'm' )
									}
									
									to( sourceLayer.p.depth_to_lay.value() ){
										uom( 'm' )
									}
									
									def fillingMaterialValue = getFillingMaterialListValue( sourceLayer.p.fill_kind_id_cli.value() )
									if (fillingMaterialValue){
										spaceFillingMaterial( fillingMaterialValue ) {
											codeSpace( 'https://www.infogeo.de/boreholeml/3.0/codelists/FillingMaterialList.xml' )
										}
									}
									
								}
							}
						}
					}
					
				}
			}
		
		}
	}	
}

//this groovy script collects all ids and text values of codelist
//and all codes of codelist
//no target is created

withTransformationContext {
	def c = _.context.collector(it)
	
	def codelist_ids = _source.p.id_cli.value()
	def codelist_text = _source.p.text_cli_en.value()
	
	c.codelist[codelist_ids] << codelist_text
	
	def codelist_code = _source.p.code_cli.value()
	c.codelist_id_code[codelist_ids] << codelist_code
}

// this groovy script searches for the (en) text of 'status_id_cli' 
// defined in the source table 'codelist' and classifies the result 

def c = _.context.collector()
def textEn = c.codelist[status_id_cli].value()

switch (textEn) {
	case 'filled':
	return 'vf'
	
	case 'cased, measurements possible':
	return 'au'
	
	case 'cased, in use':
	return 'au'
	
	case 'cased, measurements impossible':
	return 'au'
	
	case 'other ':
	return 'ka'
	
	case 'not specified':
	return 'ka'
}

// this groovy script searches for the (en) text of 'purpose_id_cli' 
// defined in the source table 'codelist' and classifies the result 

def c = _.context.collector()
def textEn = c.codelist[purpose_id_cli].value()

switch (textEn){
	case 'contaminated sites':
	return 'AUD'
	
	case 'downhole heat exchanger':
	return 'GTH'
	
	case 'hydrology':
	return 'HY'
	
	case 'other ':
	return 'Sonstiger Zweck'
	
	case 'not specified':
	return 'UN'
}
